import {Header} from "../components/Header";
import React, {useContext, useState} from "react";
import {ThingHistoricContext} from "../context/ThingHistoricContext";
import {Line} from "react-chartjs-2";
import {Col, Form, FormGroup, Row} from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";

export const ThingHistoricValues = (props) => {
    const {id, thingProperty, timeData} = useContext(ThingHistoricContext);
    const timestampArray = timeData? timeData.map(item => item.timestamp): [];
    const [formValues, setFormValues] = useState({});
    const valueData = timeData? timeData.map(item => item[thingProperty.title]): [];
    console.log(valueData)
    const data = {
        labels: timestampArray,
        datasets: [
            {
                label: thingProperty.title,
                fill: false,
                lineTension: 0.1,
                backgroundColor: 'rgba(75, 192, 192, 0.4)',
                borderColor: 'rgba(75, 192, 192,1)',
                borderCapStyle: 'butt',
                borderDash: [],
                borderDashOffset: 0.0,
                borderJoinStyle: 'miter',
                pointBorderColor: 'rgba(75, 192, 192,1)',
                pointBackgroundColor: '#fff',
                pointBorderWidth: 1,
                pointHoverRadius: 5,
                pointHoverBackgroundColor: 'rgba(75, 192, 192,1)',
                pointHoverBorderColor: 'rgba(220, 220, 220, 1)',
                pointHoverBorderWidth: 2,
                pointRadius: 1,
                pointHitRadius: 10,
                data: valueData
            }
        ]
    };
    const handleChange = (event) => {
        const {name, value} = event.target
        setFormValues(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        });
    }
    return (
        <>
            <Header title={id+ 'Historic Values'} key={'title'}></Header>
            <pre>
                {JSON.stringify(timeData)}
            </pre>
            <Form id="dataset-form" onChange={handleChange}>
                <Row>
                    <Col>
                        <FormGroup controlId="formStartDate">
                            <Form.Label>startDate*</Form.Label>
                            <Form.Control required name="name" type="text" placeholder="Enter Name"/>
                        </FormGroup>
                    </Col>
                    <Col>
                        <FormGroup controlId="formEndDate">
                            <Form.Label>endDate*</Form.Label>
                            <Form.Control required name="name" type="text" placeholder="Enter end Date"/>
                        </FormGroup>
                    </Col>
                    <Col>
                        <FormGroup>
                            <Button id="list-dataset" variant="outline-success">Update Chart</Button>
                        </FormGroup>

                    </Col>
                </Row>
            </Form>
            <Line data={data}></Line>
        </>
    )
}
