import React, {useContext} from "react";
import {useParams} from 'react-router-dom';
import { useLocation } from "react-router";
import {Header} from "../components/Header";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import "../static/styles/thingCard.css";
import "../static/styles/thing.css";
import {ThingContext} from "../context/ThingContext";
import {ThingPropertyContext} from "../context/ThingPropertyContext";

export const ThingSpecification = () => {
    const {thingPropData, disconnectSocket} = useContext(ThingPropertyContext);
    let location = useLocation();
    let { id } = useParams();
    let thing = location.state.thing;
    const socket = new WebSocket('ws://'+thing.id.split('http://')[1]);

    socket.onmessage = (event) => {
        console.log(event.data);
    };

    const thingProprtiesView = thing.properties ? Object.keys(thing.properties).map(key =>{
        return (
            <Card className={'thingCard'} key={key}>
                <Card.Header><b>{thing.properties[key].title}</b></Card.Header>
                <Card.Body>
                    <Card.Text>
                        <span>{thing.properties[key].description}</span>
                    </Card.Text>
                    <div><span><b>Unit: </b>{thing.properties[key].unit}</span></div>
                    <div><span><b>lastUpdate: </b>{thing.properties[key].updatedAt}</span></div>
                </Card.Body>
            </Card>
        );
    }): null;

    return (
        <>
            <Header title={id}></Header>
            <CardDeck className={'cardDeck'}>
                {thingProprtiesView}
            </CardDeck>
        </>
    );
}
