import React from "react";
import {useParams} from 'react-router-dom';
import { useLocation } from "react-router";
import {Header} from "../components/Header";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";

export const ThingSpecification = (props) => {
    let location = useLocation();
    let { id } = useParams();
    let thing = location.state.thing;

    console.log(thing.properties);
    const thingProprtiesView = thing.properties ? Object.keys(thing.properties).map(key =>{
        return (
            <Card styles={{
                flex:'0 0 25% !important',
                marginBottom: '2% !important'
            }}>
                <Card.Header>{thing.properties[key].title}</Card.Header>
            </Card>
        );
    }): null;

    return (
        <>
            <Header title={id}></Header>
            <CardDeck style={ {display: 'flex',
                flexWrap: 'wrap',
                marginTop: '2%'}}>
                {thingProprtiesView}
            </CardDeck>
        </>
    );
}
